import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { data, error } from 'jquery';
import { Subject } from 'rxjs';
import { EmployeeService } from 'src/app/Services/employee.service';

@Component({
  selector: 'app-viewemployee',
  templateUrl: './viewemployee.component.html',
  styleUrls: ['./viewemployee.component.css']
})
export class ViewemployeeComponent implements OnInit{

  constructor(private empserv : EmployeeService,private router : Router) { }
 
  dtOptions: DataTables.Settings={}
  dtTrigger: Subject<any> = new Subject<any>();
  response : any
  reserr   : any
  emplist  : any;
  ngOnInit(){
    this.dtOptions={
      pagingType : 'full_numbers'
    }
      this.empserv.getAllEmployees().subscribe(data=>{ 
                                                  
                                                  if(sessionStorage.getItem('response')!=null)
                                                  { 
                                                    setTimeout(() => {
                                                      this.response=sessionStorage.getItem('response')
                                                      sessionStorage.removeItem('response') 
                                                    }, 300);
                                                  }
                                                  if(sessionStorage.getItem('reserr')!=null)
                                                  {
                                                    this.reserr=sessionStorage.getItem('reserr')
                                                      setTimeout(() => {  
                                                        sessionStorage.removeItem('reserr')
                                                      }, 300);
                                                  }
                                                  this.emplist=data 
                                                  this.dtTrigger.next(null)
                                                })
  }

  getEmpById(empid : number)
  {
    this.router.navigate(['employees',empid])
  }

  retrieveassetsbyempid(empid : number)
  {
    this.router.navigate(['retrieveassetsbyempid',empid])
  }

  viewemployeehistbyid(empid : number)
  {
    this.router.navigate(['assetassignhist',empid])
  }
}
<div class="container" >
    <!-- start: PAGE HEADER -->
        <div class="row">
            <div class="col-sm-12">
                <!-- start: PAGE TITLE & BREADCRUMB -->
                <ol class="breadcrumb">
                    <li class="breadcrumb-item">
                        <a routerLink="/adminhome"> <i class="fa fa-home"></i>&nbsp;Home</a>
                    </li>
                    <li class="breadcrumb-item active" aria-current="page">View Employees</li>
                </ol>
            </div>
        </div>
        <div class="card-header">
            <h4>View Employees 
                <a routerLink="/addemployee" style="float:right;" class="btn btn-primary mb-3">Add Employee</a>
            </h4>
        </div>
        <div *ngIf="response"> <h6 class="alert alert-success">{{response}}</h6> </div>
        <div *ngIf="reserr"> <h6 class="alert alert-danger">{{reserr}}</h6> </div>
    <div class="card-body">
     <div class="table-responsive-sm">    
      <table class="table table-striped table-hover table-full-width dt-responsive nowrap" datatable [dtOptions]="dtOptions" [dtTrigger]="dtTrigger"  style="width: 100%;" id="comptable">
        <thead>
            <tr>
                <th>Sr No.</th> 
                <th>Employee</th>
                <th>Email</th>
                <th>Designation</th>
                <th>Department</th>
                <th>Company</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let emp of emplist;let i = index ">
                <td>{{i+1}}</td>
                <td>{{emp.emp_name}}</td>
                <td>{{emp.emp_email}}</td>
                <td>{{emp.designation.desig_name}}</td>
                <td>{{emp.department.dept_name}}</td>
                <td>{{emp.department.company.comp_name}}</td>
                <td>
                    <button (click)="getEmpById(emp.emp_id)" class="btn btn-primary " > <i class="fa fa-edit"></i> Edit</button> 
                    <button (click)="retrieveassetsbyempid(emp.emp_id)" class="btn btn-primary" > <i class="fa fa-edit"></i> Retrieve</button> 
                    <button (click)="viewemployeehistbyid(emp.emp_id)" class="btn btn-primary" > <i class="fa fa-edit"></i> History                               </button> 
                </td>
            </tr> 
        </tbody>
      </table>
     </div>
    </div>
  </div>
