import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Subject } from 'rxjs';
import { AssettypeService } from 'src/app/Services/assettype.service';

@Component({
  selector: 'app-viewassettype',
  templateUrl: './viewassettype.component.html',
  styleUrls: ['./viewassettype.component.css']
})
export class ViewassettypeComponent {
response : any;
dtOptions : DataTables.Settings={}
dtTrigger : Subject<any> = new Subject<any>();
atypelist : any;

reserr  : any
constructor(private atypeserv : AssettypeService,private router : Router) { }

ngOnInit()
{this.dtOptions={
  pagingType : 'full_numbers'
}
  this.atypeserv.getAllAssetTypes().subscribe(data=>{
                                                    this.atypelist=data
                                                    if(sessionStorage.getItem('response')!=null)
                                                    {
                                                      this.response=sessionStorage.getItem('response')
                                                      setTimeout(() => {
                                                        sessionStorage.removeItem('response')
                                                      }, 4000);
                                                    }
                                                    if(sessionStorage.getItem('reserr')!=null)
                                                    {
                                                      this.reserr=sessionStorage.getItem('reserr')
                                                      setTimeout(() => {
                                                        sessionStorage.removeItem('reserr')
                                                      }, 4000);
                                                    }
                                                    this.dtTrigger.next(null)
                                                  })
}

getAssetTypeById(atid : any)
{
  this.router.navigate(['assettypes',atid])
}

}
  
<div class="container" >
    <div class="row">
        <div class="col-sm-12">
            <ol class="breadcrumb">
                <li class="breadcrumb-item">
                    <a routerLink="/adminhome"> <i class="fa fa-home"></i>&nbsp;Home</a>
                </li>
                <li class="breadcrumb-item active" aria-current="page">View AssetTypes</li>
            </ol>
        </div>
    </div>
    <div class="card-header">
        <h4>View AssetTypes 
            <a routerLink="/addassettype" style="float:right;" class="btn btn-primary mb-3">Add AssetType</a>
        </h4>
    </div>
    <div *ngIf="response"> <h6 class="alert alert-success">{{response}}</h6> </div>
    <div *ngIf="reserr"> <h6 class="alert alert-danger">{{reserr}}</h6> </div>
<div class="card-body">
  <div class="table-responsive-sm">    
    <table class="table table-striped  table-hover table-full-width dt-responsive nowrap " datatable [dtOptions]="dtOptions" [dtTrigger]="dtTrigger" style="width: 100%;" id="comptable">
        <thead>
            <tr>
                <th>Sr No.</th> 
                <th>Asset Type</th>
                <th>Action</th>
            </tr>
        </thead>
    <tbody>
        <tr *ngFor="let type of atypelist; let i = index ">
            <td>{{i+1}}</td>
            <td>{{type.type_name}}</td>
            <td>
                <button (click)="getAssetTypeById(type.type_id)" class="btn btn-primary" > <i class="fa fa-edit"></i> Edit</button> 
            </td>
        </tr> 
    </tbody>
  </table>
 </div>
</div>
</div>
