import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Subject } from 'rxjs';
import { CompanyService } from 'src/app/Services/company.service';

@Component({
  selector: 'app-viewcompany',
  templateUrl: './viewcompany.component.html',
  styleUrls: ['./viewcompany.component.css']
})
export class ViewcompanyComponent {
  complist : any;
  comp_id  : any; 

  response : any;
  reserr   : any;

  dtOptions: DataTables.Settings = {};
  dtTrigger: Subject<any> = new Subject<any>();

  constructor(private compserv : CompanyService, private router : Router ) { }

  ngOnInit(): void {
    this.dtOptions={
        pagingType : 'full_numbers'
    }
    this.compserv.getAllCompanies().subscribe(data=>{
                                                      this.complist=data 
                                                      if(sessionStorage.getItem('response')!=null)
                                                      {
                                                        this.response=sessionStorage.getItem('response')
                                                        setTimeout(() => {
                                                          sessionStorage.removeItem('response')
                                                        }, 300);
                                                      }
                                                      if(sessionStorage.getItem('reserr')!=null)
                                                      {
                                                        this.reserr=sessionStorage.getItem('reserr')
                                                        setTimeout(() => {
                                                          sessionStorage.removeItem('reserr')
                                                        }, 300);
                                                      } 
                                                      // initiate our data table
                                                       this.dtTrigger.next(null)
                                                    });
  }

  ngOnDestroy(): void {
    this.dtTrigger.unsubscribe();
  }

  getCompById(cid : any)
  {
    this.router.navigate(['companies',cid]);
  }
}
  
<div class="container" >
    <div class="row">
        <div class="col-sm-12">
            <ol class="breadcrumb">
                <li class="breadcrumb-item">
                    <a routerLink="/adminhome"> <i class="fa fa-home"></i>&nbsp;Home</a>
                </li>
                <li class="breadcrumb-item active" aria-current="page">View Company</li>
            </ol>
        </div>
    </div>
    <div class="card-header">
        <h4>View Companies 
            <a routerLink="/company" style="float:right;" class="btn btn-primary mb-3">Add Company</a>
        </h4>
    </div>
    <div *ngIf="response"><h6 class="alert alert-success">{{response}}</h6></div>
    <div *ngIf="reserr"><h6 class="alert alert-danger">{{reserr}}</h6></div>
<div class="card-body">
  <div class="table-responsive-sm">    
    <table class="table table-striped table-hover table-full-width dt-responsive nowrap " datatable [dtOptions]="dtOptions" [dtTrigger]="dtTrigger" style="width: 100%;" id="comptable">
        <thead>
            <tr>
                <th>Sr No.</th> 
                <th>Company</th>
                <th>Action</th>
            </tr>
        </thead>
    <tbody>
        <tr *ngFor="let comp of complist; let i = index ">
            <td>{{i+1}}</td>
            <td>{{comp.comp_name}}</td>
            <td>
                <button (click)="getCompById(comp.comp_id)" class="btn btn-primary" > <i class="fa fa-edit"></i> Edit</button> 
            </td>
        </tr> 
    </tbody>
  </table>
 </div>
</div>
</div>
