import { Component } from '@angular/core';
import { Route, Router } from '@angular/router';
import { Subject } from 'rxjs';
import { DepartmentService } from 'src/app/Services/department.service';


@Component({
  selector: 'app-viewdepartment',
  templateUrl: './viewdepartment.component.html',
  styleUrls: ['./viewdepartment.component.css']
})
export class ViewdepartmentComponent {

  constructor(private deptserv : DepartmentService,private router : Router ) {}
  response : any
  reserr : any
  deptlist : any;
  dtOptions: DataTables.Settings = {};
  dtTrigger: Subject<any> = new Subject<any>();
  ngOnInit(): void {
    this.dtOptions={
      pagingType : 'simple_numbers',
  }
    this.deptserv.getAllDepartments().subscribe({
      next:(data)=> {
        this.deptlist=data
                    if(sessionStorage.getItem('response')!=null)
                    {
                      this.response=sessionStorage.getItem('response')
                      setTimeout(() => {
                        sessionStorage.removeItem('response')
                        this.response=""
                      }, 3000);
                    }
                    if(sessionStorage.getItem('reserr')!=null)
                    {
                      this.reserr=sessionStorage.getItem('reserr')
                      setTimeout(() => {
                        sessionStorage.removeItem('reserr')
                        this.reserr=""
                      }, 3000); 
                    } 
                    // initiate our data table
                    this.dtTrigger.next(null);
      }
    }
    );
  }

  ngOnDestroy(): void {
    this.dtTrigger.unsubscribe();
  }

  getDeptById(did : number)
  {
    this.router.navigate(['departments',did]);
  }
}
<div class="container" >
	
    <!-- start: PAGE HEADER -->
        <div class="row">
            <div class="col-sm-12">
                <!-- start: PAGE TITLE & BREADCRUMB -->
                <ol class="breadcrumb">
                    <li class="breadcrumb-item">
                        <a routerLink="/adminhome"> <i class="fa fa-home"></i>&nbsp;Home</a>
                    </li>
                    <li class="breadcrumb-item active" aria-current="page">View Department</li>
                </ol>
            </div>
        </div>
        <div class="card-header">
            <h4>View Departments 
                <a routerLink="/adddepartment" style="float:right;" class="btn btn-primary mb-3">Add Department</a>
            </h4>
    </div>
    <div *ngIf="response"> <h6 class="alert alert-success">{{response}}</h6> </div>
    <div *ngIf="reserr"> <h6 class="alert alert-danger">{{reserr}}</h6> </div>
    <div class="card-body">
            <div class="table-responsive-sm">    
      <table class="table table-striped table-hover table-full-width dt-responsive nowrap" datatable [dtOptions]="dtOptions" [dtTrigger]="dtTrigger"  style="width: 100%;" id="comptable">
        <thead>
            <tr>
                <th>Sr No.</th> 
                <th>Department</th>
                <th>Company</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let dept of deptlist;let i = index ">
                <td>{{i+1}}</td>
                <td>{{dept.dept_name}}</td>
                <td>{{dept.company.comp_name}}</td>
                <td>
                    <button (click)="getDeptById(dept.dept_id)" class="btn btn-primary" > <i class="fa fa-edit"></i> Edit</button> 
                </td>
            </tr> 
        </tbody>
      </table>
     </div>
    </div>
  </div>
